// Converts gripper-space joint values to ROS-space joint values
// gripper-space:  real-gambit compatible
// ROs-space:      Gazebo / Rviz compatible
double gripos2ROSpos(double pos) {
    double r = GRIPPER_HUB_RADIUS;
    double l = GRIPPER_LINKAGE_LENGTH;
    double t = M_PI/2.0 - pos;
    return r * cos(t) + sqrt(l*l - pow(r * sin(t), 2.0));
}

// Converts ROS-space joint values to gripper-space joint values
double ROSpos2gripos(double pos) {
    for (int i = 0; i < 1833-1; ++i) {
        if (pos >= grippos[i] && pos < grippos[i+1]) {
            if (i==0)
                return ROSpos[i];
            else if (i==1833-1-1)
                return ROSpos[1831-1];
            else
                return ROSpos[i-1];
        }
    }
}

